import axios from "axios"

const instance = axios.create({
    baseURL: 'https://www.themealdb.com/api/json/v1/1/'
})

export const getCategories = async () => {
    let response = await instance.get('categories.php')
}

export const getMealsByCategory = category => {
    let response = await instance.get(`filter.php?c=${category}`)
}

export const getMealById = async id => {
    let response = await instance.get(`lookup.php?i=${id}`)
}

export const getMealsByName = async name => {
    let response = await instance.get(`search.php?s=${name}`)
}