import React from 'react'
import { Link } from 'react-router-dom'

function MealsItem(props) {
    const {
        strMeal: name,
        strMealThumb: image,
        idMeal: id
    } = props

    return (
        <div className="card">
            <div className="card-image">
                <img src={image} alt={name} />
            </div>
            <div className="card-content">
                <span className="card-title">{name.length > 40 ? `${name.slice(0, 30)}...` : name}</span>
            </div>
            <div className="card-action">
                <Link to={`/recipe/${id}`} className='btn purple lighten-1'>
                    Watch recipe
                </Link>
            </div>
        </div>
    )
}

export default MealsItem
